client_script:
let totalAmount = 0;
let studentTikPrice = 0;
let generalTikPrice = 0;

let generalTicketCount = 0;
let studentTicketCount = 0;

let ticketsLeft = 0;

let totalSelectedTickets = 0;

const queryString = window.location.search;
const parameters = new URLSearchParams(queryString);
const confName = parameters.get('conference')

const addBreadCrumb = (url, label) => {
    $('.page-breadcrumbs')[0].innerHTML = `
<div class="breadcrumb-container container">
	<nav aria-label="breadcrumb">
		<ol class="breadcrumb" itemscope="" itemtype="http://schema.org/BreadcrumbList">


				<li itemprop="itemListElement" itemscope="" itemtype="http://schema.org/ListItem" class="breadcrumb-item">
					<a itemprop="item" href=" ` + url +`">
						<span itemprop="name">` + label + `</span>
						<meta itemprop="position" content="1">
					</a>
				</li>

			<li itemprop="itemListElement" itemscope="" itemtype="http://schema.org/ListItem" class="breadcrumb-item active" aria-current="page">
				<span itemprop="item">
					<span itemprop="name">Book tickets</span>
					<meta itemprop="position" content="2">
				</span>
			</li>
		</ol>
	</nav>
</div>`
}

const getTicketPrices = async () => {
    return new Promise((resolve, rej) => {
        frappe.call({
            method: 'indiafoss.indiafoss.doctype.conference.conference.get_ticket_data',
            args: {
                'doc_name': confName,
            },
            callback: function(r) {
                if (!r.exc) {
                    resolve(r.message);
                } else {
                    reject(r.exception);
                }
            }
        });
    });
}

const setTicketPriceInForm = (ticketPrices) => {
    if (ticketPrices.student_ticket_price === 0 && ticketPrices.general_ticket_price === 0) {
        frappe.msgprint('Conference not found, please check if you have correct URL');
        return;
    }

    ticketsLeft = ticketPrices.max_tickets - ticketPrices.tickets_booked;

    // if ticket price is not available for a field hide it.
    if (ticketPrices.student_ticket_price === 0) {
        $('#student_ticket_container').hide();
        frappe.web_form.set_df_property('student_tickets', 'hidden', 1);
        frappe.web_form.set_df_property('student_ticket_price', 'hidden', 1);
    }

    if (ticketPrices.general_ticket_price === 0) {
        $('#general_ticket_container').hide();
        frappe.web_form.set_df_property('general_tickets', 'hidden', 1);
        frappe.web_form.set_df_property('general_ticket_price', 'hidden', 1);
    }

    studentTikPrice = ticketPrices.student_ticket_price;
    generalTikPrice = ticketPrices.general_ticket_price;
    frappe.web_form.set_value(['student_ticket_price'], ticketPrices.student_ticket_price)
    frappe.web_form.set_value(['general_ticket_price'], ticketPrices.general_ticket_price)
}  

const updateTotalTicketCount = () => {
    totalSelectedTickets = generalTicketCount + studentTicketCount;
    if (totalSelectedTickets > ticketsLeft) {
        frappe.msgprint(`There are only ${ticketsLeft} tickets left for the conference`, title='Error');
        return false;
    }
    return true;
}

frappe.web_form.after_load = async () => {
    let bcUrl = confName.split("-");
    bcUrl = "/" + bcUrl[0] + "/" + bcUrl[1];
    addBreadCrumb(bcUrl, confName);
    $(".submit-btn").html(`Pay ₹${totalAmount}`);

    $(".submit-btn").click(function(){
        if (totalAmount === 0) {
            frappe.msgprint("Please select at least one ticket to proceed", title='Select a ticket');
            return false;
        }
    })

    // Remove discard button
    document.querySelectorAll(".discard-btn").forEach(el => el.remove());


    frappe.web_form.set_df_property('general_tickets', 'hidden', 1);
    frappe.web_form.set_df_property('student_tickets', 'hidden', 1);

    $('#student_ticket_count').html(studentTicketCount);
    // student tickets button event listeners
    $("#reduce_student_ticket_count").click(function(){
        if (studentTicketCount == 0){
            return;
        }
        studentTicketCount -= 1;
        updateTotalTicketCount()
        frappe.web_form.set_value(['student_tickets'], studentTicketCount);
        $('#student_ticket_count').html(studentTicketCount);
    });

    $("#increase_student_ticket_count").click(function(){
        studentTicketCount += 1;
        let validCount = updateTotalTicketCount();

        if (!validCount) {
            studentTicketCount -= 1;
            updateTotalTicketCount();
        }

        frappe.web_form.set_value(['student_tickets'], studentTicketCount);
        $('#student_ticket_count').html(studentTicketCount);
    });

    // general ticket button event listeners
    $('#general_ticket_count').html(generalTicketCount);
    $("#reduce_general_ticket_count").click(function(){
        if (generalTicketCount == 0){
            frappe.web_form.set_value(['general_tickets'], generalTicketCount);
            return;
        }
        generalTicketCount -= 1;
        updateTotalTicketCount()
        frappe.web_form.set_value(['general_tickets'], generalTicketCount);
        $('#general_ticket_count').html(generalTicketCount);
    });

    $("#increase_general_ticket_count").click(function(){
        generalTicketCount += 1;
        let validCount = updateTotalTicketCount();

        if (!validCount) {
            generalTicketCount -= 1;
            updateTotalTicketCount();
        }
        frappe.web_form.set_value(['general_tickets'], generalTicketCount);
        $('#general_ticket_count').html(generalTicketCount);
    });

    frappe.web_form.set_df_property('conference', 'read_only', 1);

    try {
        ticketPrices = await getTicketPrices();
        setTicketPriceInForm(ticketPrices);
    } catch {
        frappe.throw("Conference not found " )
    }
    frappe.web_form.on('conference',  async (field, value) => {
        if (value) {
            ticketPrices = await getTicketPrices();
            setTicketPriceInForm(ticketPrices);
        }
    });

    frappe.web_form.set_df_property('conference', 'read_only', 1);
    frappe.web_form.set_df_property('student_ticket_price', 'read_only', 1);
    frappe.web_form.set_df_property('general_ticket_price', 'read_only', 1);
    frappe.web_form.set_df_property('total_amount', 'read_only', 1);

    frappe.web_form.on('student_tickets',  async (field, value) => {
        if (value >= 0) {
            let general_ticket_count = frappe.web_form.get_value(['general_tickets']);

            if (general_ticket_count === undefined)
                general_ticket_count = 0

            totalAmount = (general_ticket_count * generalTikPrice) + value * studentTikPrice
            frappe.web_form.set_value(['total_amount'], totalAmount)
            // Update buy button text
            $(".submit-btn").html(`Pay ₹${totalAmount}`);
        }
    });

    frappe.web_form.on('general_tickets',  async (field, value) => {
        if (value >= 0) {
            let student_ticket_count = frappe.web_form.get_value(['student_tickets']);

            if (student_ticket_count === undefined)
                student_ticket_count = 0

            totalAmount = (student_ticket_count * studentTikPrice) + value * generalTikPrice
            $(".submit-btn").html(`Pay ₹${totalAmount}`);
            frappe.web_form.set_value(['total_amount'], totalAmount)
        }
    });
}

frappe.web_form.validate = () => {
    if (totalSelectedTickets <= 0){ 
        return false;
    }
    return true;
}

$("[data-fieldname='conference']").prop('readonly', true);


const urlParams = new URLSearchParams(window.location.search);
var url = '/'

if(urlParams.has('c') && urlParams.has('y')){
    url = url + urlParams.get('c') + '/' + urlParams.get('y')

}


frappe.web_form.after_save = () => {
    window.location.href = url
 }


var label = 'Home'
if(urlParams.has('t')){
    label = urlParams.get('t')

}


$('.page-breadcrumbs')[0].innerHTML = `
<div class="breadcrumb-container container">
	<nav aria-label="breadcrumb">
		<ol class="breadcrumb" itemscope="" itemtype="http://schema.org/BreadcrumbList">


				<li itemprop="itemListElement" itemscope="" itemtype="http://schema.org/ListItem" class="breadcrumb-item">
					<a itemprop="item" href=" ` + url +`">
						<span itemprop="name">` + label + `</span>
						<meta itemprop="position" content="1">
					</a>
				</li>

			<li itemprop="itemListElement" itemscope="" itemtype="http://schema.org/ListItem" class="breadcrumb-item active" aria-current="page">
				<span itemprop="item">
					<span itemprop="name">Proposal</span>
					<meta itemprop="position" content="2">
				</span>
			</li>
		</ol>
	</nav>
</div>`

function formatTime(time, id){
    document.getElementById(id).innerHTML = moment(time, "HH:mm:ss").format("h A");
}

// Check if conference is accepting proposals or not
frappe.call({
    method: 'indiafoss.indiafoss.doctype.conference_talks.conference_talks.is_accepting_proposals',
    args: {
        'conference': urlParams.get('conference'),
    },
    callback: function(r) {
        console.log(r);
        if (!r.exc) {
            if (r.message === false) {
                setTimeout(function(){
                 	window.location.href = window.location.origin;
                }, 5000);
                frappe.throw(`<b>${urlParams.get('c')}</b> conference has stopped accepting proposals, for more details contact <b>Admin</b>`)
            }
        }
    }
});
