function venue(){
var section = $('.section[data-section-template="Conference Venue"]');
  $('html, body').animate({
    scrollTop: $(section).offset().top
  }, 'slow');
}

function formatTime(time, id){
    document.getElementById(id).innerHTML = moment(time, "HH:mm:ss").format("h A");
}

function formatDate(date, id){
    document.getElementById(id).innerHTML = moment(date).format("Do MMMM YYYY")
}

function formatDateRange(startdate, enddate, id){
    start = moment(startdate).format("Do MMM YYYY").split(" ")
    end = moment(enddate).format("Do MMM YYYY").split(" ")

    startdate = start[0]
    enddate = end[0]

    year = ""

    if(start[1] != end[1]){
        startdate += " " + start[1]
        enddate += " " + end[1]
    }else{
        year += " " + start[1]
    }

    if(start[2] != end[2]){
        startdate += " " + start[2]
        enddate += " " + end[2]
    }else{
        year += " " + start[2]
    }

    document.getElementById(id).innerHTML = startdate + " to " + enddate + " " + year
}

css:
.talk-card .card-title {
    font-weight: 600;
    margin-bottom: 1rem;
}

.talk-card .meta {
    display: flex;
    align-items: center;
    justify-content: space-between;
    width: 100%;
    margin-bottom: 1rem;
}

.talk-card .category, .talk-card .time {
    font-size: var(--font-size-xs);
    display: inline-block;
    padding: 0.2rem 0.7rem;
    border-radius: 0.5rem;
    background-color: var(--gray-100);
    color: var(--gray-600);
    font-weight: 500;
}

.talk-card .category {
    background-color: var(--green-100);
    color: var(--green-800);
}

.talk-card .time {
    font-size: var(--font-size-sm);
    color: var(--gray-800);
}

.talk-card.break {
    background: #eee;
}


.talk-author {
    display: flex;
    align-items: center;
    gap: 1rem
}

.talk-author img {
    width: 4rem;
    height: 4rem;
    object-fit: cover;
    border-radius: 50%;
}

.talk-author .fullname {
    font-weight: 500;
}

main_section_html:
{% macro talk_card(talk) %}
<div class="talk-card card mt-8 {{ 'break' if talk.get('is_break') }}">
  <div class="card-body">
    <div class="meta">
        <span><div class="time">{{ frappe.utils.format_time(talk.start_time, "hh:mm a") }} - {{ frappe.utils.format_time(talk.end_time, "hh:mm a") }} </div> {% if talk.room_name %} <b>{{ talk.room_name }}</b> {% endif %} </span>
        {% if talk.category %}
        <span class="category">{{ talk.category }}</span>
        {% endif %}
    </div>

    <h5 class="card-title">{{ talk.title }}</h5>
    {% if not talk.get('is_break') %}
    <div class="talk-author">
        {% if talk.photo %}
        <img src="{{ talk.photo }}" alt="Profile photo of {{ talk.full_name }}">
        {% endif %}
        <div>
            {% if talk.full_name %}
                <div class="fullname">{{ talk.full_name }}</div>
            {% endif %}
            {% if talk.designation %}
            <div class="designation">{{ talk.designation }}</div>
            {% endif %}
        </div>
    </div>
    {% if talk.get('name') %}
    <a class='stretched-link' href="/proposal/{{ talk.name }}"></a>
        <span class="sr-only">View Talk</span>
    {% endif %}
    {% endif %}
  </div>
</div>
{% endmacro %}

<div class="py-12">
    <h1>Schedule</h1>
    <p class="lead">All the times mentioned in the schedule are in Indian Standard Time (IST) timezone and 24-hour clock.</p>
</div>

<div class="btn-group" role="group" aria-label="Basic example">
    {% for talkday in days %}
  <a href="?day={{ talkday.day }}" class="btn btn-secondary {{ 'active' if day|str == talkday.day|str else '' }}"> {{ frappe.utils.formatdate(talkday.day, "dd MMM")}}</a>
  {% endfor %}
</div>

<div class="row">


{% for talk in hall1talks %}
   <div class="col-md-6">
    {{ talk_card(talk) }}
    </div>
{% endfor %}
</div>
