javascript:
function venu(){
var section = $('.section[data-section-template="Conference Venu"]');
  $('html, body').animate({
    scrollTop: $(section).offset().top
  }, 'slow');
}

css:
.talk-card .card-title {
    font-weight: 600;
    margin-bottom: 1rem;
}

.talk-card .meta {
    display: flex;
    align-items: center;
    justify-content: space-between;
    width: 100%;
    margin-bottom: 1rem;
}

.talk-card .category, .talk-card .time {
    font-size: var(--font-size-xs);
    display: inline-block;
    padding: 0.2rem 0.7rem;
    border-radius: 0.5rem;
    background-color: var(--gray-100);
    color: var(--gray-600);
    font-weight: 500;
}

.talk-card .category {
    background-color: var(--green-100);
    color: var(--green-800);
}

.talk-card .time {
    font-size: var(--font-size-sm);
    color: var(--gray-800);
}

.talk-card.break {
    background: #eee;
}


.talk-author {
    display: flex;
    align-items: center;
    gap: 1rem
}

.talk-author img {
    width: 4rem;
    height: 4rem;
    object-fit: cover;
    border-radius: 50%;
}

.talk-author .fullname {
    font-weight: 500;
}

main_section_html:
{% macro talk_card(talk) %}
<div class="talk-card card mt-8 {{ 'break' if talk.get('is_break') }}">
  <div class="card-body">
    <div class="meta">
        <div class="time">{{ talk.start_time }} - {{ talk.end_time }}</div>
        {% if talk.category %}
        <span class="category">{{ talk.category }}</span>
        {% endif %}
    </div>

    <h5 class="card-title">{{ talk.title }}</h5>
    {% if not talk.get('is_break') %}
    <div class="talk-author">
        {% if talk.photo %}
        <img src="{{ talk.photo }}" alt="Profile photo of {{ talk.full_name }}">
        {% endif %}
        <div>
            {% if talk.full_name %}
                <div class="fullname">{{ talk.full_name }}</div>
            {% endif %}
            {% if talk.designation %}
            <div class="designation">{{ talk.designation }}</div>
            {% endif %}
        </div>
    </div>
    {% if talk.get('slug') %}
    <a class='stretched-link' href="/2022/cfp/submissions/{{ talk.slug }}"></a>
        <span class="sr-only">View Talk</span>
    {% endif %}
    {% endif %}
  </div>
</div>
{% endmacro %}

<div class="py-12">
    <h1>Schedule</h1>
    <p class="lead">All the times mentioned in the schedule are in Indian Standard Time (IST) timezone and 24-hour clock.</p>
</div>

<div class="btn-group" role="group" aria-label="Basic example">
    {% for talkday in days %}
  <a href="?day={{ talkday.day }}" class="btn btn-secondary {{ 'active' if day == talkday.day else '' }}">Day {{talkday.day}}</a>
  {% endfor %}
</div>

<div class="row">


{% for talk in hall1talks %}
   <div class="col-md-6">
    {{ talk_card(talk) }}
    </div>
{% endfor %}
</div>
