template:
{% set markdown = frappe.db.sql("SELECT description FROM `tabConference` where title = %(title)s and year=%(year)s ", args, as_dict=true )%}
{% set x = frappe.utils.markdown(markdown[0].description )%}

{{ x }}

{% set sponsors = frappe.db.sql("SELECT s.link, s.image, c.sponsors_title FROM `tabConference` c inner JOIN `tabConference Sponsors` s on c.name = s.parent  where c.title = %(title)s and c.year=%(year)s ", args, as_dict=true )%}
{% if sponsors %}
<h2 class="section-title">Sponsors</h2>
<p class="section-description">{{ sponsors[0].sponsors_title }} 

<div class="row mt-5 mx-md-n3 align-items-center">
{% for sponsor in sponsors %}

    <div class="col-6 col-md-4 col-xs-12 py-2 p-md-3">
        <a href="{{ sponsor.link }}" rel="noopener noreferrer" target="blank">
            <img alt="{{ sponsor.company }}" src="{{ sponsor.image }}">
        </a>
</div>
{% endfor %}
</div>
{% endif %}

{% set partners = frappe.db.sql("SELECT s.link, s.image, c.partners_title FROM `tabConference` c inner JOIN `tabConference Partners` s on c.name = s.parent  where c.title = %(title)s and c.year=%(year)s ", args, as_dict=true )%}

{% if partners %}
<h2 class="section-title">Community Partners</h2>
<p class="section-description">{{ partners[0].partners_title }}

<div class="row mt-5 mx-md-n3 align-items-center">
{% for partner in partners %}
    <div class="col-4 col-md-2 py-2 p-md-3">
        <a href="{{ partner.link }}" rel="noopener noreferrer" target="blank">
            <img alt="{{ partner.company }}" src="{{ partner.image }}">
        </a>
    </div>

{% endfor %}
</div>
{% endif %}

{% set venu = frappe.db.sql("SELECT venu, venu_details, venu_map_link, start_time, end_time, date FROM `tabConference` c inner JOIN `tabConference Partners` s on c.name = s.parent  where c.title = %(title)s and c.year=%(year)s ", args, as_dict=true )%}
{% if venu %}
<div class = "row">
    <h2 class="section-title col">Conference Venu</h2>
</div>
<div class = "row venue">
    <div class = "col-lg-8 col-md-8">
        <div class = "text">
        <h3 class = "place-heading">{{ venu[0].venu }}</h3>
        <p class ="place"><a id="map" href = "{{ venu[0].venu_map_link }}">
           {{ venu[0].venu_details }}
        </a></p>
        </div>
    </div>
    <div class = "col-lg-4 col-md-4">
        <div class = "text">
        <h3 class = "place-heading">Date</h3>
        <p class = "place">
            {{ venu[0].date }}<br>
            {{ venu[0].start_time }} to {{ venu[0].end_time }}
        </p>
        </div>
    </div>
</div>

<style>
    #map {
      color:#4C5A67;
      text-decoration: underline;
    }
    .venue {
        height: auto;
         position: relative; 
    }
    .venue::before {
        content: "";
        background-image: url('https://storage.googleapis.com/gweb-uniblog-publish-prod/images/Maps_Pin_FullColor.max-1000x1000.png');
        background-position: center center;
        background-size: cover;
        opacity: 0.1;
        position: absolute;
        top: 0px;
        right: 0px;
        bottom: 0px;
        left: 0px;
    }
    .text {
        padding-top: 3vw;
    }
    @media screen and (max-width: 414px) {
        .venue {
            margin: 0 1.7vw 0 1.7vw;
        }
        .place {
            font-size: 3.7vw;
        }
        .place-heading {
            font-size: 4.7vw;
        }
    }
    @media screen and (max-width: 576px) and (min-width: 414px) {
        .venue {
            margin: 0 1.7vw 0 1.7vw;
        }
        .place {
            font-size: 3.3vw;
        }
        .place-heading {
            font-size: 4.3vw;
        }
    }
</style>
{% endif %}
