template:
{% set markdown = frappe.db.sql("SELECT description FROM `tabConference` where title = %(title)s and year=%(year)s ", args, as_dict=true )%}
{% set x = frappe.utils.markdown(markdown[0].description )%}

{{ x }}

{% set sponsors = frappe.db.sql("SELECT s.link, s.image, c.sponsors_title FROM `tabConference` c inner JOIN `tabConference Sponsors` s on c.name = s.parent  where c.title = %(title)s and c.year=%(year)s ", args, as_dict=true )%}
{% if sponsors %}
<h2 class="section-title">Sponsors</h2>
<p class="section-description">{{ sponsors[0].sponsors_title }} 

{% for sponsor in sponsors %}
<div class="row mt-5 mx-md-n3 align-items-center"><div class="col-6 col-md-4 col-xs-12 py-2 p-md-3">
<a href="{{ sponsor.link }}" rel="noopener noreferrer" target="blank">
<img alt="{{ sponsor.company }}" src="{{ sponsor.image }}">
</a>
</div></div>
{% endfor %}
{% endif %}

{% set partners = frappe.db.sql("SELECT s.link, s.image, c.partners_title FROM `tabConference` c inner JOIN `tabConference Partners` s on c.name = s.parent  where c.title = %(title)s and c.year=%(year)s ", args, as_dict=true )%}

{% if partners %}
<h2 class="section-title">Community Partners</h2>
<p class="section-description">{{ partners[0].partners_title }}

{% for partner in partners %}
<div class="row mt-5 mx-md-n3 align-items-center"><div class="col-4 col-md-2 py-2 p-md-3">
<a href="{{ partner.link }}" rel="noopener noreferrer" target="blank">
<img alt="{{ partner.company }}" src="{{ partner.image }}">
</a>
</div></div>
{% endfor %}
{% endif %}
